import time

class Solution:
    def maxOperations(self, s: str) -> int:

        if '1' not in s:
            return 0

        lst = list(s)
        n = len(lst)
        count = 0
        end = n-1

        while True:
            a = None
            acted = False
            for i in range(lst.index('1'), end):
                if not a and lst[i] == '1' and lst[i+1] == '0':
                    a = i
                    continue
                if a and (lst[i + 1] == '1' or i == end-1):
                    b = i
                    lst[a], lst[b] = lst[b], lst[a]
                    end = b
                    count += 1
                    a = None
                    acted = True
            if not acted:
                break


        # while True:
        #     acted = False
        #     for i in range(lst.index('1'), end):
        #         if lst[i] == '1' and lst[i+1] == '0':
        #             try:
        #                 j = lst.index('1', i+1) - 1
        #             except ValueError:
        #                 j = n-1
        #             lst[i], lst[j] = lst[j], lst[i]
        #             end = j
        #             count += 1
        #             acted = True

        #     if not acted:
        #         break

        return count

def main():
    sol = Solution()
    # input = ""
    input = "1001101"
    begin = time.perf_counter()
    print(sol.maxOperations(input))
    finish = time.perf_counter()
    print("Time to complete: ", finish - begin)


if __name__ == "__main__":
    main()
